import React, { Component } from "react"

import Module from "./Entity/Module";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      modules: []
      };
  }

    async componentDidMount() {
      try {
        const res = await fetch('http://localhost:8000/api/modules/');
        const modules = await res.json();
        console.log(modules)
        this.setState({
          modules
        });
      } catch (e) {
        console.log(e);
    }
    }

    renderModules = () => {
      const newItems = this.state.modules;
      return newItems.map(module => (
        <li 
          key={module.id}
          className="list-group-item d-flex justify-content-between align-items-center"
        >
          <Module code={module.code} 
            nom={module.nom}
            cours={module.cours}
            seances={module.seances}
            nb_heures_be={module.nb_heures_be}
            nb_heures_ci={module.nb_heures_ci}
            nb_heures_tp={module.nb_heures_tp}
            nb_heures_total={module.nb_heures_total}/>
        </li>

      ));
    };

    render() {
      return (
        <main className="content">
        <div className="row">
          <div className="col-md-6 col-sm-10 mx-auto p-0">
            <div className="card p-3">
              <ul className="list-group list-group-flush">
                {this.renderModules()}
              </ul>
            </div>
          </div>
        </div>
      </main>
      )
    }
  }
  
export default App;
